module rps_confirmation_screen(
    input clk,
    input bot_has_chosen,
    input [1:0]selection,
    input [12:0]pixel_index,
    output reg [15:0]oled_colour
);

    wire clk6p25m;
    
    flexi_clk clk6p25m_instantiate(
        .clk(clk),
        .m_const(32'd7),
        .my_clk(clk6p25m)
    );
    parameter ROCK = 2'b00, PAPER = 2'b01, SCISSORS = 2'b10;
    wire [15:0] oled_colour_bg;
    
    wire [15:0] oled_icon_rock;
    wire [15:0] oled_icon_paper;
    wire [15:0] oled_icon_scissors;
    
    skillscreen_confirmation_background background_selection_render (.clk(clk6p25m),.pixel_index(pixel_index),.oled_colour(oled_colour_bg));
    skillscreen_icon_rock icon_rock_render(.clk(clk6p25m),.pixel_index(pixel_index),.sprite_x(37),.sprite_y(28),.background_pixel(oled_colour_bg),.oled_colour(oled_icon_rock));
    skillscreen_icon_paper icon_paper_render(.clk(clk6p25m),.pixel_index(pixel_index),.sprite_x(37),.sprite_y(28),.background_pixel(oled_colour_bg),.oled_colour(oled_icon_paper));
    skillscreen_icon_scissors icon_scissors_render(.clk(clk6p25m),.pixel_index(pixel_index),.sprite_x(37),.sprite_y(28),.background_pixel(oled_colour_bg),.oled_colour(oled_icon_scissors));

    always @ (*)begin
        if (bot_has_chosen)begin
            case(selection)
                ROCK: oled_colour <= oled_icon_rock;
                PAPER: oled_colour <= oled_icon_paper;
                SCISSORS: oled_colour <= oled_icon_scissors;
                default: oled_colour <= oled_colour_bg;
            endcase
        end
        else oled_colour <= oled_colour_bg;
    end
endmodule

